import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Header = ({ user, activeUsers, onLogout }) => {
  const location = useLocation();

  const navItems = [
    { path: '/tasks', label: 'Tasks', icon: 'üìã' },
    { path: '/stats', label: 'Analytics', icon: 'üìä' },
    { path: '/focus', label: 'Focus', icon: '‚è∞' },
    { path: '/collaboration', label: 'Collaborate', icon: 'üë•' }
  ];

  return (
    <header className="header">
      <div className="header-left">
        <Link to="/" className="logo">
          ÔøΩ Smart Productivity Dashboard
        </Link>
      </div>
      
      <nav className="header-nav">
        {navItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`nav-link ${location.pathname === item.path ? 'active' : ''}`}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
          </Link>
        ))}
      </nav>

      <div className="header-right">
        <div className="active-users">
          <span className="users-indicator">üë• {activeUsers.length} online</span>
        </div>
        
        <div className="user-info">
          <span className="welcome-text">Welcome, <strong>{user.username}</strong></span>
          <button onClick={onLogout} className="logout-btn">
            <span>üö™</span>
            Logout
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
