import React, { useState, useEffect } from 'react';

const GoogleCalendar = ({ user }) => {
  const [events, setEvents] = useState([]);
  const [error] = useState(''); // Removed setError since it's not used

  // Mock events for demonstration (replace with actual API call when OAuth is properly configured)
  const mockEvents = [
    {
      id: '1',
      summary: 'Team Stand-up Meeting',
      start: { dateTime: new Date(Date.now() + 1 * 60 * 60 * 1000).toISOString() }, // 1 hour from now
      end: { dateTime: new Date(Date.now() + 1.5 * 60 * 60 * 1000).toISOString() },
      location: 'Conference Room A',
      description: 'Daily team synchronization meeting'
    },
    {
      id: '2',
      summary: 'Project Review',
      start: { dateTime: new Date(Date.now() + 4 * 60 * 60 * 1000).toISOString() }, // 4 hours from now
      end: { dateTime: new Date(Date.now() + 5 * 60 * 60 * 1000).toISOString() },
      location: 'Virtual Meeting',
      description: 'Quarterly project review with stakeholders'
    },
    {
      id: '3',
      summary: 'Lunch Break',
      start: { date: new Date().toISOString().split('T')[0] }, // Today (all-day)
      end: { date: new Date().toISOString().split('T')[0] },
      description: 'Personal time'
    },
    {
      id: '4',
      summary: 'Client Presentation',
      start: { dateTime: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString() }, // Tomorrow
      end: { dateTime: new Date(Date.now() + 25 * 60 * 60 * 1000).toISOString() },
      location: 'Client Office',
      description: 'Present Q4 deliverables to client'
    }
  ];

  useEffect(() => {
    // Load mock events for demo
    setEvents([...mockEvents]);
  }, []); // Empty dependency array is fine since mockEvents is static

  const formatDateTime = (dateTime, isAllDay = false) => {
    if (isAllDay) {
      return new Date(dateTime).toLocaleDateString();
    }
    return new Date(dateTime).toLocaleString();
  };

  const getEventTimeString = (event) => {
    if (event.start.date) {
      // All-day event
      return `üìÖ ${formatDateTime(event.start.date, true)}`;
    } else {
      // Timed event
      const start = new Date(event.start.dateTime);
      const end = new Date(event.end.dateTime);
      const today = new Date();
      
      if (start.toDateString() === today.toDateString()) {
        return `‚è∞ Today ${start.toLocaleTimeString()} - ${end.toLocaleTimeString()}`;
      } else {
        return `‚è∞ ${start.toLocaleDateString()} ${start.toLocaleTimeString()}`;
      }
    }
  };

  const getEventStatus = (event) => {
    const now = new Date();
    const start = new Date(event.start.dateTime || event.start.date);
    const end = new Date(event.end.dateTime || event.end.date);

    if (now >= start && now <= end) {
      return { status: 'ongoing', icon: 'üî¥', text: 'Ongoing' };
    } else if (start > now) {
      return { status: 'upcoming', icon: 'üü°', text: 'Upcoming' };
    } else {
      return { status: 'past', icon: '‚ö™', text: 'Past' };
    }
  };

  const handleConnectCalendar = () => {
    // Redirect to Google OAuth with calendar scope
    window.location.href = '/auth/google';
  };

  return (
    <div className="card">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
        <h3>üìÖ Calendar Overview</h3>
        <div style={{ display: 'flex', gap: '0.5rem' }}>
          <button 
            onClick={() => setEvents([...mockEvents])} 
            className="btn btn-secondary"
            style={{ fontSize: '0.8rem', padding: '0.3rem 0.6rem' }}
          >
            üîÑ Refresh
          </button>
          <button 
            onClick={handleConnectCalendar} 
            className="btn btn-primary"
            style={{ fontSize: '0.8rem', padding: '0.3rem 0.6rem' }}
          >
            üîó Connect Google Calendar
          </button>
        </div>
      </div>

      {error && (
        <div style={{ 
          background: '#fee', 
          color: '#c33', 
          padding: '0.75rem', 
          borderRadius: '6px', 
          marginBottom: '1rem',
          fontSize: '0.9rem'
        }}>
          ‚ö†Ô∏è {error}
        </div>
      )}

      <div style={{ 
        background: '#e3f2fd', 
        padding: '0.75rem', 
        borderRadius: '6px', 
        marginBottom: '1rem',
        fontSize: '0.85rem',
        color: '#1565c0'
      }}>
        üí° <strong>Demo Mode:</strong> Showing sample events. Click "Connect Google Calendar" to link your real calendar.
      </div>

      {loading ? (
        <div style={{ textAlign: 'center', padding: '2rem', color: '#666' }}>
          <div className="loading-spinner" style={{ margin: '0 auto 1rem' }}></div>
          <p>Loading calendar events...</p>
        </div>
      ) : events.length === 0 ? (
        <div style={{ 
          textAlign: 'center', 
          padding: '1.5rem', 
          background: '#f8f9fa', 
          borderRadius: '8px',
          color: '#666'
        }}>
          <div style={{ fontSize: '1.5rem', marginBottom: '0.5rem' }}>‚úÖ</div>
          <p>No upcoming events</p>
        </div>
      ) : (
        <div>
          <div style={{ marginBottom: '1rem', fontSize: '0.9rem', color: '#666' }}>
            üìç Showing {events.length} upcoming events
          </div>
          <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
            {events.map((event, index) => {
              const eventStatus = getEventStatus(event);
              return (
                <div 
                  key={event.id || index}
                  style={{
                    padding: '0.75rem',
                    marginBottom: '0.5rem',
                    border: '1px solid #eee',
                    borderRadius: '6px',
                    background: eventStatus.status === 'ongoing' ? '#fff3cd' : '#fff',
                    borderLeft: `4px solid ${eventStatus.status === 'ongoing' ? '#ff6b6b' : eventStatus.status === 'upcoming' ? '#4caf50' : '#ccc'}`
                  }}
                >
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '0.25rem' }}>
                    <div style={{ fontWeight: '500', color: '#333', flex: 1 }}>
                      {event.summary || 'No Title'}
                    </div>
                    <span style={{ 
                      fontSize: '0.7rem', 
                      background: eventStatus.status === 'ongoing' ? '#ff6b6b' : eventStatus.status === 'upcoming' ? '#4caf50' : '#ccc',
                      color: 'white',
                      padding: '0.2rem 0.4rem',
                      borderRadius: '10px',
                      marginLeft: '0.5rem'
                    }}>
                      {eventStatus.icon} {eventStatus.text}
                    </span>
                  </div>
                  
                  <div style={{ fontSize: '0.8rem', color: '#666', marginBottom: '0.25rem' }}>
                    {getEventTimeString(event)}
                  </div>
                  
                  {event.location && (
                    <div style={{ fontSize: '0.8rem', color: '#666' }}>
                      üìç {event.location}
                    </div>
                  )}
                  
                  {event.description && (
                    <div style={{ 
                      fontSize: '0.8rem', 
                      color: '#666', 
                      marginTop: '0.5rem',
                      maxHeight: '40px',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis'
                    }}>
                      {event.description.slice(0, 100)}...
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default GoogleCalendar;
