import React, { useState, useEffect } from 'react';

const GoogleCalendar = ({ user }) => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isAuthorized, setIsAuthorized] = useState(false);

  // Google Calendar API configuration
  const DISCOVERY_DOC = 'https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest';
  const SCOPES = 'https://www.googleapis.com/auth/calendar.readonly';

  useEffect(() => {
    // Initialize Google API
    const initializeGoogleAPI = async () => {
      try {
        if (window.gapi) {
          await window.gapi.load('client:auth2', initializeGapiClient);
        }
      } catch (error) {
        console.error('Error initializing Google API:', error);
        setError('Failed to initialize Google Calendar API');
      }
    };

    const initializeGapiClient = async () => {
      try {
        await window.gapi.client.init({
          discoveryDocs: [DISCOVERY_DOC],
          clientId: process.env.REACT_APP_GOOGLE_CLIENT_ID || '340527516217-r38irpf87frm1an78fgihv31540vstut.apps.googleusercontent.com',
          scope: SCOPES
        });

        const authInstance = window.gapi.auth2.getAuthInstance();
        setIsAuthorized(authInstance.isSignedIn.get());

        // Listen for sign-in state changes
        authInstance.isSignedIn.listen(setIsAuthorized);
      } catch (error) {
        console.error('Error initializing GAPI client:', error);
        setError('Failed to initialize Google Calendar client');
      }
    };

    // Load Google API script if not already loaded
    if (!window.gapi) {
      const script = document.createElement('script');
      script.src = 'https://apis.google.com/js/api.js';
      script.onload = initializeGoogleAPI;
      document.body.appendChild(script);
    } else {
      initializeGoogleAPI();
    }
  }, []);

  const handleAuthClick = async () => {
    try {
      const authInstance = window.gapi.auth2.getAuthInstance();
      await authInstance.signIn();
      setIsAuthorized(true);
      fetchEvents();
    } catch (error) {
      console.error('Error signing in:', error);
      setError('Failed to sign in to Google Calendar');
    }
  };

  const handleSignoutClick = async () => {
    try {
      const authInstance = window.gapi.auth2.getAuthInstance();
      await authInstance.signOut();
      setIsAuthorized(false);
      setEvents([]);
    } catch (error) {
      console.error('Error signing out:', error);
      setError('Failed to sign out');
    }
  };

  const fetchEvents = async () => {
    if (!isAuthorized) return;

    setLoading(true);
    setError('');

    try {
      const now = new Date();
      const timeMin = now.toISOString();
      const timeMax = new Date(now.getTime() + (7 * 24 * 60 * 60 * 1000)).toISOString(); // Next 7 days

      const response = await window.gapi.client.calendar.events.list({
        calendarId: 'primary',
        timeMin: timeMin,
        timeMax: timeMax,
        showDeleted: false,
        singleEvents: true,
        maxResults: 10,
        orderBy: 'startTime',
      });

      const events = response.result.items || [];
      setEvents(events);
    } catch (error) {
      console.error('Error fetching calendar events:', error);
      setError('Failed to fetch calendar events');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (isAuthorized) {
      fetchEvents();
    }
  }, [isAuthorized]);

  const formatDateTime = (dateTime, isAllDay = false) => {
    if (isAllDay) {
      return new Date(dateTime).toLocaleDateString();
    }
    return new Date(dateTime).toLocaleString();
  };

  const getEventTimeString = (event) => {
    if (event.start.date) {
      // All-day event
      return `📅 ${formatDateTime(event.start.date, true)}`;
    } else {
      // Timed event
      const start = new Date(event.start.dateTime);
      const end = new Date(event.end.dateTime);
      const today = new Date();
      
      if (start.toDateString() === today.toDateString()) {
        return `⏰ Today ${start.toLocaleTimeString()} - ${end.toLocaleTimeString()}`;
      } else {
        return `⏰ ${start.toLocaleDateString()} ${start.toLocaleTimeString()}`;
      }
    }
  };

  const getEventStatus = (event) => {
    const now = new Date();
    const start = new Date(event.start.dateTime || event.start.date);
    const end = new Date(event.end.dateTime || event.end.date);

    if (now >= start && now <= end) {
      return { status: 'ongoing', icon: '🔴', text: 'Ongoing' };
    } else if (start > now) {
      return { status: 'upcoming', icon: '🟡', text: 'Upcoming' };
    } else {
      return { status: 'past', icon: '⚪', text: 'Past' };
    }
  };

  return (
    <div className="card">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
        <h3>📅 Google Calendar</h3>
        <div style={{ display: 'flex', gap: '0.5rem' }}>
          {isAuthorized && (
            <button 
              onClick={fetchEvents} 
              className="btn btn-secondary"
              disabled={loading}
              style={{ fontSize: '0.8rem', padding: '0.3rem 0.6rem' }}
            >
              {loading ? '🔄' : '🔄'} Refresh
            </button>
          )}
          {isAuthorized ? (
            <button 
              onClick={handleSignoutClick} 
              className="btn btn-outline"
              style={{ fontSize: '0.8rem', padding: '0.3rem 0.6rem' }}
            >
              🚪 Sign Out
            </button>
          ) : (
            <button 
              onClick={handleAuthClick} 
              className="btn btn-primary"
              style={{ fontSize: '0.8rem', padding: '0.3rem 0.6rem' }}
            >
              🔗 Connect Calendar
            </button>
          )}
        </div>
      </div>

      {error && (
        <div style={{ 
          background: '#fee', 
          color: '#c33', 
          padding: '0.75rem', 
          borderRadius: '6px', 
          marginBottom: '1rem',
          fontSize: '0.9rem'
        }}>
          ⚠️ {error}
        </div>
      )}

      {!isAuthorized ? (
        <div style={{ 
          textAlign: 'center', 
          padding: '2rem', 
          background: '#f8f9fa', 
          borderRadius: '8px',
          color: '#666'
        }}>
          <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>📅</div>
          <p style={{ marginBottom: '1rem' }}>Connect your Google Calendar to see upcoming events</p>
          <button onClick={handleAuthClick} className="btn btn-primary">
            🔗 Connect Google Calendar
          </button>
        </div>
      ) : loading ? (
        <div style={{ textAlign: 'center', padding: '2rem', color: '#666' }}>
          <div className="loading-spinner" style={{ margin: '0 auto 1rem' }}></div>
          <p>Loading calendar events...</p>
        </div>
      ) : events.length === 0 ? (
        <div style={{ 
          textAlign: 'center', 
          padding: '1.5rem', 
          background: '#f8f9fa', 
          borderRadius: '8px',
          color: '#666'
        }}>
          <div style={{ fontSize: '1.5rem', marginBottom: '0.5rem' }}>✅</div>
          <p>No upcoming events in the next 7 days</p>
        </div>
      ) : (
        <div>
          <div style={{ marginBottom: '1rem', fontSize: '0.9rem', color: '#666' }}>
            📍 Showing next {events.length} events (7 days)
          </div>
          <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
            {events.map((event, index) => {
              const eventStatus = getEventStatus(event);
              return (
                <div 
                  key={event.id || index}
                  style={{
                    padding: '0.75rem',
                    marginBottom: '0.5rem',
                    border: '1px solid #eee',
                    borderRadius: '6px',
                    background: eventStatus.status === 'ongoing' ? '#fff3cd' : '#fff',
                    borderLeft: `4px solid ${eventStatus.status === 'ongoing' ? '#ff6b6b' : eventStatus.status === 'upcoming' ? '#4caf50' : '#ccc'}`
                  }}
                >
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '0.25rem' }}>
                    <div style={{ fontWeight: '500', color: '#333', flex: 1 }}>
                      {event.summary || 'No Title'}
                    </div>
                    <span style={{ 
                      fontSize: '0.7rem', 
                      background: eventStatus.status === 'ongoing' ? '#ff6b6b' : eventStatus.status === 'upcoming' ? '#4caf50' : '#ccc',
                      color: 'white',
                      padding: '0.2rem 0.4rem',
                      borderRadius: '10px',
                      marginLeft: '0.5rem'
                    }}>
                      {eventStatus.icon} {eventStatus.text}
                    </span>
                  </div>
                  
                  <div style={{ fontSize: '0.8rem', color: '#666', marginBottom: '0.25rem' }}>
                    {getEventTimeString(event)}
                  </div>
                  
                  {event.location && (
                    <div style={{ fontSize: '0.8rem', color: '#666' }}>
                      📍 {event.location}
                    </div>
                  )}
                  
                  {event.description && (
                    <div style={{ 
                      fontSize: '0.8rem', 
                      color: '#666', 
                      marginTop: '0.5rem',
                      maxHeight: '40px',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis'
                    }}>
                      {event.description.slice(0, 100)}...
                    </div>
                  )}
                  
                  {event.htmlLink && (
                    <div style={{ marginTop: '0.5rem' }}>
                      <a 
                        href={event.htmlLink} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        style={{ 
                          fontSize: '0.8rem', 
                          color: '#667eea',
                          textDecoration: 'none'
                        }}
                      >
                        🔗 Open in Google Calendar
                      </a>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default GoogleCalendar;
